mapper.select:

and o."dealerId" =  #{dealerId} 
and o."dealerStatus" = '2'
and o."colorCode" = #{colorCode}
and o."upholsteryCode" = #{upholsteryCode}
and o."configCode" = #{configCode}
and o."addCode" = #{addCode}

select count(*) amount,d."dealerName", va."variantDescription"||' '|| co."colorDescription"||' '||up."upholsteryDescription"||' '||o."yearType"||' '||o."addCode" as description from sales.order o, sales.upholstery up, sales.variant va, sales.color co, sales.dealer d where o."dealerStatus" != '0'
and o."matchStatus" = #{value}
and o."colorCode" = co."colorCode" and o."upholsteryCode" = up."upholsteryCode" and o."configCode" = va."variantCode" and o."dealerId" = d."dealerId" group by o."colorCode", o."upholsteryCode",o."configCode", o."addCode",d."dealerId", d."dealerName",o."yearType", co."colorDescription", up."upholsteryDescription", va."variantDescription"

select o."orderId",mt."materialsId", mt.vin , d."dealerName", d."region", d."province", va."variantDescription" as "configDesc", co."colorDescription" as "colorDesc", up."upholsteryDescription" as "upholsteryDesc", o."yearType",o."addCode" as "addCodes", o."dealerStatus", o."matchStatus", mt."apw" from sales.order o, sales.materials mt, sales.dealer d, sales.upholstery up, sales.variant va, sales.color co where o."materialsId" = mt."materialsId" and o."dealerId" = d."dealerId" and mt."colorCode" = co."colorCode" and mt."upholsteryCode" = up."upholsteryCode" and mt."configCode" = va."variantCode" and o."matchStatus" = #{matchStatus} and o."dealerStatus" != '0'
and d."region" = #{region}
and d."province" = #{province}
